notifications[msg.notification_id].message = msg.message ?? ""
notifications[msg.notification_id].alertType = msg.alertType ?? "info"

global.set("notifications", notifications)
msg.notifications = notifications
return msg

if (msg.notification?.event?.service === "create") {
    return [msg, null]
} else if (msg.notification?.event?.service === "dismiss") {
    msg.notifications = global.get("notifications") ?? {}
    delete msg.notifications[msg.notification.event.service_data.notification_id]
    return [null, msg]
} else if (msg.notifications[msg.payload?.event?.tag]) {
    delete msg.notifications[msg.payload?.event?.tag]
    return [null, msg];
}

let notifications = global.get("notifications") ?? {}

if (msg.notification_id in notifications) {
    return msg
}

msg.notifications = global.get("notifications") ?? {}
let notification_id = msg.payload?.event?.tag ?? ""

if (msg.notifications[notification_id]) {
    return { notification_id: notification_id, dismiss: true }
}

if ((msg.lights?.length ?? 0) != 0 && msg.ambiance_config?.ambiances_type) {
    msg.reset = true
    if (msg.action_type != "stop") {
        let msgs = []
        for (let light of msg.lights) {
            if (light.attributes?.supported_color_modes?.includes("rgb")) {
                let ambiance_type = msg.ambiance_config.ambiances_type[msg.action_type]
                let hs = hsRandomValue(ambiance_type.color[0], Math.round(Math.random() * ambiance_type.variance[0]), 0, 360)
                let saturation = randomValue(ambiance_type.color[1], Math.round(Math.random() * ambiance_type.variance[1]), 0, 100)
                let brightness = randomValue(ambiance_type.color[2], Math.round(Math.random() * ambiance_type.variance[2]), 0, 100)
                let delay = 1000 + Math.round(Math.random() * 14000)
                msgs.push({
                    entity_id: light.entity_id,
                    action_type: msg.action_type,
                    ambiance_config: msg.ambiance_config,
                    delay: delay,
                    payload: {
                        data: {
                            entity_id: light.entity_id,
                            hs_color: [hs, saturation],
                            brightness_pct: brightness,
                            transition: Math.round(delay / 100) / 10
                        }
                    }
                })
            }
        }
        return [msgs, msg]
    } else {
        return [null, msg]
    }
}

function hsRandomValue(value, variance, lowRange, highRange) {
    let random = Math.random()
    return random < 0.5 ? (value - variance < lowRange ? highRange - variance : value - variance) : (value + variance > highRange ? lowRange + variance : value + variance)
}

function randomValue(value, variance, lowRange, highRange) {
    let random = Math.random()
    return random < 0.5 ? (value - variance < lowRange ? lowRange : value - variance) : (value + variance > highRange ? highRange : value + variance)
}

if (msg.action_type != "stop") {
    let ambiance_type = msg.ambiance_config.ambiances_type[msg.action_type]
    let hs = hsRandomValue(ambiance_type.color[0], Math.round(Math.random() * ambiance_type.variance[0]), 0, 360)
    let saturation = randomValue(ambiance_type.color[1], Math.round(Math.random() * ambiance_type.variance[1]), 0, 100)
    let brightness = randomValue(ambiance_type.color[2], Math.round(Math.random() * ambiance_type.variance[2]), 0, 100)
    let delay = 1000 + Math.round(Math.random() * 14000)

    msg.delay = delay
    if (msg.action_type == "thunderstorm" && delay < 1500) {
        msg.payload = {
            data: {
                entity_id: msg.entity_id,
                effect: "Strobe epilepsy!"
            }
        }        
    } else {
        msg.payload = {
            data: {
                entity_id: msg.entity_id,
                hs_color: [hs, saturation],
                brightness_pct: brightness,
                transition: Math.round(delay / 100) / 10
            }
        }
    }

    return msg
}

function hsRandomValue(value, variance, lowRange, highRange) {
    let random = Math.random()
    return random < 0.5 ? (value - variance < lowRange ? highRange - variance : value - variance) : (value + variance > highRange ? lowRange + variance : value + variance)
}

function randomValue(value, variance, lowRange, highRange) {
    let random = Math.random()
    return random < 0.5 ? (value - variance < lowRange ? lowRange : value - variance) : (value + variance > highRange ? highRange : value + variance)
}

if (msg.action_type && msg.linkedSpeaker) {
    let speaker = global.get("home_configuration")[msg.linkedSpeaker] ?? {}

    if ( msg.ambiance_config?.path?.start !== "" && msg.ambiance_config?.path?.end !== ""
    ) {
        if (msg.action_type == "stop") {
            let sound = "ambiance_stop.mp3"
