            msg = {
                linkedArea: msg.linkedArea,
                volumeLevel: msg.ambianceVolumeLevel ?? 0.5,
                link: msg.ambiance_config.path.start + msg.action_type + msg.ambiance_config.path.end
            }
        }
        return msg
    }
}

