function getTargetTemp(schedule, currentTime) {
    let mvt = msg.originalLinkedClass == "motion"
    let motionHeater = context.get("motionHeater") || {}
    let scheduleHour = ""
    let target = "no_temp"
    let wait = false
    for (let s of schedule) {
        if (currentTime < Object.keys(s)[0]) {
            break
        }
        scheduleHour = Object.keys(s)[0]
        wait = s[Object.keys(s)[0]][1]
        if (!s[Object.keys(s)[0]][1] || mvt || motionHeater[msg.linkedArea + "_" + scheduleHour]) {
            target = s[Object.keys(s)[0]][0]
        }
    }
    if (mvt && wait) {
        motionHeater[msg.linkedArea + "_" + scheduleHour] = true
        context.set("motionHeater", motionHeater)
    } else if (!wait) {
        motionHeater = {}
        context.set("motionHeater", motionHeater)
    }
    return target
}

