function setTemplateVariables(condition) {
    if (condition.includes("global.")) {
        let globals_to_inject = global.keys().filter(g => condition.includes("global." + g))
        if (globals_to_inject.length != 0) {
            let injection = '{% set global = {'
            for (let g of globals_to_inject) {
                injection += '"' + g + '":' + JSON.stringify(global.get(g) || {}) + ','
            }
            injection = injection.slice(0, -1)
            injection += '} %}'
            condition = injection + condition
            return condition
        }
    } else {
        return condition
    }
}

