let persons = msg.persons

for (let p of persons) {
    if (action.includes(p.name + "_")) {
        let training = action.split("_")[1]
        if (activities[p.name]?.[training]?.[2]) {
            if (activities[p.name][training][0] < activities[p.name][training][1]) {
                activities[p.name][training][0]++
            }
            if (activities[p.name][training][0] == activities[p.name][training][1]) {
                activities[p.name][training][2] = false
            }

        }
    }
}

msg.payload = { "data": { "value": JSON.stringify(activities) } }
return msg

msg.notification_id = "module_healthy"
if (msg.payload == "off") {
    msg.alertType = "info"
    msg.title = "Module santé désactivé"
    msg.message = "Module santé désactivé, vous ne recevrez plus de notifications d'activités"
    msg.sendTo = msg.persons.filter(p => p.sport).map(p => p.name)
    msg.sendMobile = true
} else {
    msg.dismiss = true
}
return msg

let activities = JSON.parse(global.get("homeassistant").homeAssistant.states["input_text.json_activities"].state)
let random = Math.random() * 100
let twd = two_week_day()
let persons = msg.persons
let msgs = []

for (let person of persons) {

    if (!activities[person.name]) {
        activities[person.name] = {}
        activities[person.name]["days"] = 0
    }

    let objective_calendar = person.sport.objective_calendar
    let todays_objectives = Object.keys(objective_calendar).filter(a => objective_calendar[a][twd])

    for (let o of Object.keys(objective_calendar)) {
        if (twd == 1 || !(o in activities[person.name])) {
            activities[person.name][o] = [0, 0, false]
        }
    }

    let objective_conditions = person.sport.objective_conditions ?? []
    objective_conditions = validate_objective_conditions(objective_conditions, todays_objectives)
    todays_objectives = calculate_objective_conditions(objective_conditions, person, todays_objectives)

    let objective_limits = person.sport.objective_limits

    for (let o of todays_objectives) {
        let translations = "objective_translations" in person.sport && o in person.sport.objective_translations ? person.sport.objective_translations[o] : o
        let todays_objective_limit = []

        activities[person.name][o][1]++
        activities[person.name][o][2] = true

        if (Array.isArray(objective_limits[o])) {
            todays_objective_limit = calculate_training(person, objective_limits[o])
        } else if (typeof objective_limits[o] === "object") {
            for (let keys of Object.keys(objective_limits[o])) {
                let objectiveName = keys.charAt(0).toUpperCase() + keys.slice(1)
                todays_objective_limit.push(objectiveName + ": " + calculate_training(person, objective_limits[o][keys]))
            }
            todays_objective_limit = todays_objective_limit.join("\n")
        }
        msgs.push({ objective: { name: o, translations: translations, todays_objective_limit: todays_objective_limit, person: person.name } })
    }
    if (activities[person.name]["days"] < person.sport.days_to_ojective) {
        activities[person.name]["days"]++
    }
}
msg = { "payload": { "data": { "value": JSON.stringify(activities) } } }
return [msgs, msg]

function validate_objective_conditions(objective_conditions, todays_objectives) {
    let vp = []
    for (let p of objective_conditions) {
        let count = 0
        for (let w of p.split(" ")) {
            if (todays_objectives.includes(w)) {
                count++
            }
        }
        if (count == 2) {
            vp.push(p)
        }
    }
    return vp
}

function calculate_objective_conditions(objective_conditions, person, todays_objectives) {
    for (let p of objective_conditions) {
        let p_split = p.trim().split(" ")
        if (p_split.length == 4 && p_split.includes("or")) {
            let percentage = p_split[0].replace("%", "")
            todays_objectives = todays_objectives.filter(t => t != (random < parseInt(percentage) ? p_split[3] : p_split[1]))
        } else if (p_split.length == 3 && p_split.includes("to")) {
            let percentage = ((person.sport.days_to_ojective - activities[person.name]["days"]) / person.sport.days_to_ojective) * 100
            todays_objectives = todays_objectives.filter(t => t != (random < parseInt(percentage) ? p_split[2] : p_split[0]))
        }
    }
    return todays_objectives
}

function calculate_training(person, objective_limits) {
    let objective = ""
    if (isNaN(objective_limits[0]) && isNaN(objective_limits[1])) {
        let min_g_split = objective_limits[0].trim().split(" ")
        let max_g_split = objective_limits[1].trim().split(" ")
        let percentage = activities[person.name]["days"] / person.sport.days_to_ojective
        objective = parseInt((max_g_split[0] - min_g_split[0]) * percentage + parseInt(min_g_split[0])) + " " + max_g_split[1]
    } else {
        let min_g = objective_limits[0]
        let max_g = objective_limits[1]
        let percentage = activities[person.name]["days"] / person.sport.days_to_ojective
        let serie_number = parseInt(5 + percentage * 3.99)
        let repetition_number = parseInt(((max_g - min_g) * percentage + min_g) / serie_number)
        let rest_number = parseInt(((max_g - min_g) * percentage + min_g) % serie_number)
        objective = repetition_number + " * " + serie_number + (rest_number == 0 ? "" : " + " + rest_number % serie_number)
    }
    return objective
}

function two_week_day() {
    let date = new Date()
    return Math.floor((date.getTime() - new Date(date.getFullYear() + "-01-01").getTime()) / (3600000 * 24)) % 14 + 1
}

let title = "Sport: " + (msg.objective.todays_objective_limit.length != 0 ? "une séance de " + msg.objective.translations + " est prévue" : msg.objective.translations)
let message = msg.objective.todays_objective_limit.length != 0 ? "Votre objectif: \n" + msg.objective.todays_objective_limit : ""

msg = {
    title: title,
    message: message,
    sendTo: [msg.objective.person],
    sendMobile: true,
    data: {
        actions: [{ "action": msg.objective.person + "_" + msg.objective.name, "title": "Fait avec amour" }],
        timeout: 60 * 60 * 20
    }
}

return msg

//,{ "action": msg.objective.person + "_", "title": "pas fait" }

let activities = JSON.parse(global.get("homeassistant").homeAssistant.states["input_text.json_activities"].state)
let twd = two_week_day()
let persons = msg.persons
let msgs = []

for (let person of persons) {
    let person_activities_keys = Object.keys(activities[person.name]).filter(a => a != "days")
    let activities_not_finished = []

    for (let activity of person_activities_keys) {
        if (activities[person.name][activity][0] != activities[person.name][activity][1]) {
            activities_not_finished.push({ activity: activity, number: (activities[person.name][activity][1] - activities[person.name][activity][0]) })
        }
    }

    let person_day = activities[person.name]["days"] - 1 < 0 ? 0 : activities[person.name]["days"] - 1
    let objective_calendar = person.sport.objective_calendar
    let objective_limits = person.sport.objective_limits

    for (var i = 0; i < activities_not_finished.length; i++) {
        for (var j = 0; j < twd; j++) {
            if (
                activities_not_finished[i]["number"] > 0
                && activities_not_finished[i]["activity"] in objective_calendar
                && objective_calendar[activities_not_finished[i]["activity"]][twd - j]
            ) {
                activities_not_finished[i]["number"]--
                let activity = activities_not_finished[i]["activity"]
                let translation = "objective_translations" in person.sport && activity in person.sport.objective_translations ? person.sport.objective_translations[activity] : activity

                if (Array.isArray(objective_limits[activity])) {
                    msgs.push(
                        {
                            objective: {
                                name: activity,
                                translations: translation,
                                todays_objective_limit: translation + ": " + calculate_training(person, objective_limits[activity], person_day - j),
                                person: person.name
                            }
                        }
                    )
                } else if (typeof objective_limits[activity] === "object") {
                    let objective_limit = []
                    for (let keys of Object.keys(objective_limits[activity])) {
                        let objectiveName = keys.charAt(0).toUpperCase() + keys.slice(1)
                        objective_limit.push(objectiveName + ": " + calculate_training(person, objective_limits[activity][keys], person_day - j))
                    }
                    objective_limit = objective_limit.join("\n")
                    msgs.push(
                        {
                            objective: {
                                name: activity,
                                translations: translation,
                                todays_objective_limit: objective_limit,
                                person: person.name
                            }
                        }
                    )
                }
            }
        }
    }
}
return [msgs]

function calculate_training(person, objective_limits, person_day) {
    let objective = ""
    if (isNaN(objective_limits[0]) && isNaN(objective_limits[1])) {
        let min_g_split = objective_limits[0].trim().split(" ")
        let max_g_split = objective_limits[1].trim().split(" ")
        let percentage = person_day / person.sport.days_to_ojective
        objective = parseInt((max_g_split[0] - min_g_split[0]) * percentage + parseInt(min_g_split[0])) + " " + max_g_split[1]
    } else {
        let min_g = objective_limits[0]
        let max_g = objective_limits[1]
        let percentage = person_day / person.sport.days_to_ojective
        let serie_number = parseInt(5 + percentage * 3.99)
        let repetition_number = parseInt(((max_g - min_g) * percentage + min_g) / serie_number)
        let rest_number = parseInt(((max_g - min_g) * percentage + min_g) % serie_number)
        objective = repetition_number + " * " + serie_number + (rest_number == 0 ? "" : " + " + rest_number % serie_number)
    }
    return objective
}

function two_week_day() {
    let date = new Date()
    return Math.floor((date.getTime() - new Date(date.getFullYear() + "-01-01").getTime()) / (3600000 * 24)) % 14 + 1
}

let activities = msg.payload
let persons = msg.persons
let html = ""
let msgs = []

for (let person of persons) {
    html += buildHTML(person, activities)
}

let gap = 0
for (let i = 0; i < 4; i++) {
    msgs.push({ payload: { data: { entity_id: "input_text.health_stats_" + (i + 1), value: html.substring(gap, (gap + 254)) } } })
    gap += 254
}
return [msgs]

function buildHTML(person, activities) {
    let table = ["", ""]
    let percent = [0, 0]
    let sub_html = ""
    for (let key of Object.keys(person.sport.objective_limits)) {
        if (activities[person.name][key]) {
            table[0] += '<th style="width:20%">' + (person.sport?.objective_translations?.[key] ?? key) + '</th>'
            table[1] += '<td>' + (activities[person.name][key][0] === activities[person.name][key][1] ? '✅' : activities[person.name][key][0] + "/" + activities[person.name][key][1]) + '</td>'
            percent[0] += activities[person.name][key][0]
            percent[1] += activities[person.name][key][1]
        }
    }
    sub_html += '<center><b>' + 'Objectifs santé ' + person.name + " - " + "Jour " + activities[person.name]["days"] + "/" + person.sport.days_to_ojective + " : "
    sub_html += (percent[1] == 0 ? 0 : parseInt(100 * percent[0] / percent[1])) + '% atteints cette semaine' + '</b></center><hr>'
    sub_html += '<table style="width:100%;text-align:center"><tr>' + table[0] + '</tr><tr>' + table[1] + '</tr></table>'
    return sub_html
}

