let config = {};

config.rules = [
    {
        property: "attributes.config.linkedClass",
        logic: "is",
        value: "light",
        valueType: "str"
    },
    {
        property: "attributes.config.linkedArea",
        logic: "is",
        value: msg.linkedArea,
        valueType: "str"
    }
];

config.outputType = "array";
config.outputEmptyResults = false;
config.outputLocationType = "msg";
config.outputLocation = "lights";

msg.payload = config;
