var lights = msg.lights;
var msgs = [];

for (let light of lights) {
    let modes = "supported_color_modes" in light.attributes && Array.isArray(light.attributes.supported_color_modes) ? light.attributes.supported_color_modes : [];
    if (modes.some(mode => mode == "hs")) {
        msgs.push({
            light: light.entity_id,
            current_state: light.state,
            count: msg.lightEffect.count,
            hue1: msg.lightEffect.hue1,
            hue2: msg.lightEffect.hue2,
            delay: msg.lightEffect.count * 1000
        });
    }
}

return [msgs];

let isLightEffect = "lightEffect" in msg && typeof msg.lightEffect == "object"
    && "count" in msg.lightEffect && typeof msg.lightEffect.count == "number"
    && "hue1" in msg.lightEffect && typeof msg.lightEffect.hue1 == "number"
    && "hue2" in msg.lightEffect && typeof msg.lightEffect.hue2 == "number";

if (isLightEffect) {
    let area_motions = global.get("area_motions") || {};
    let msgs = [];

    Object.entries(area_motions).forEach(([key, value]) => {
        if (value != "off") {
            msgs.push({ linkedArea: key, lightEffect: msg.lightEffect });
        }
    });

    return [msgs];
}

