if (activities["run"][2] && activities["walk"][2]) {
    activities["walk"][1]--;
    activities["walk"][2] = false;
    msg.payload = activities;
    return msg;
}

let activities = JSON.parse(global.get("homeassistant").homeAssistant.states["input_text.json_activities"].state || "{}");
let totalUp = parseInt(activities["walk"][0]) + parseInt(activities["run"][0]) + parseInt(activities["fitness"][0]) +parseInt(activities["stretching"][0]) +parseInt(activities["mesure"][0]);
let totalDo = parseInt(activities["walk"][1]) + parseInt(activities["run"][1]) + parseInt(activities["fitness"][1]) +parseInt(activities["stretching"][1]) +parseInt(activities["mesure"][1]);
let percent = totalDo !== 0 ? parseInt(100*totalUp/totalDo) : 0;

msg.notification_id = "resetSportWeek";
msg.title = "Sport et Santé : Récapitulatif de la semaine";
msg.message = 'Objectifs santé de la semaine: ' + percent + '% atteints ' + (percent === 100 ? '🎊🎉' : '');
msg.alertType = "info";
msg.data = {};

return msg;

msg.notification_id = "resetSportWeek";
msg.dismiss = true;

return msg;

let msgs = [];

for (let person of msg.persons) {
    if ("mobileNotifications" in person && person.mobileNotifications != "") {
        let mess = {
            service: person.mobileNotifications,
            payload: {
                data: {
                    title: "title" in msg ? msg.title : "",
                    message: "message" in msg ? msg.message : ""
                }
            }
        };
        if ("data" in msg) {
            mess.payload.data["data"] = msg.data;
        }
        msgs.push(mess);
    }
}

return [msgs];

if ("sendMobile" in msg && msg.sendMobile) {
    let config = {};

    config.rules = [
        {
            property: "attributes.config.linkedClass",
            logic: "is",
            value: "person",
            valueType: "str"
        }
    ];

    config.outputType = "array";
