let nightLightTime = time_to_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.night_light_time"].state);
let riseLightTime = time_to_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.rise_light_time"].state);
let sunAngleLightOffTime = time_to_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_angle_light_off_time"].state);
let currentTime = time_to_minutes(new Date().toString().slice(16, 21));

nightLightTime = nightLightTime <= sunAngleLightOnTime ? 1440 - sunAngleLightOnTime + nightLightTime : nightLightTime - sunAngleLightOnTime;
riseLightTime = 1440 - sunAngleLightOnTime + riseLightTime;
sunAngleLightOffTime = 1440 - sunAngleLightOnTime + sunAngleLightOffTime;
currentTime = sunAngleLightOnTime <= currentTime ? currentTime - sunAngleLightOnTime : 1440 - sunAngleLightOnTime + currentTime;
sunAngleLightOnTime = 0;

if (lightTimeStatus && currentTime >= sunAngleLightOnTime && currentTime < nightLightTime) {
    msg.currentTimeStatus = min_is1(pctCalc(sunAngleLightOnTime, nightLightTime, currentTime, false));
} else if (lightTimeStatus && currentTime >= nightLightTime && currentTime < riseLightTime) {
    msg.currentTimeStatus = 100;
} else if (lightTimeStatus && currentTime >= riseLightTime && currentTime < sunAngleLightOffTime) {
    msg.currentTimeStatus = min_is1(pctCalc(riseLightTime, sunAngleLightOffTime, currentTime, true));
} else if (lightTimeStatus) {
