}
return msg;

function time_to_minutes(timeTxt) {
    timeTxt = timeTxt.split(":");
    return parseInt(timeTxt[0]) * 60 + parseInt(timeTxt[1]);
}

function min_is1(number) {
    return number < 1 ? 1 : number;
}

function pctCalc(min, max, nb, reverse) {
    return nb < min ? (reverse ? 100 : 0) : nb > max ? (reverse ? 0 : 100) : reverse ? (max - nb) / Math.abs(min - max) * 100 : (nb - min) / Math.abs(min - max) * 100;
}

if (msg.currentTimeStatus == 0 && msg.dayLight) {
    msg.brightnessPct = msg.brightRange[1];
} else if (msg.currentTimeStatus == 0 && msg.lightWhenCloudy) {
    let sunAngleLightOnTime = time_to_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_angle_light_on_time"].state);
    let sunAngleLightOffTime = time_to_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_angle_light_off_time"].state);
    let currentTime = time_to_minutes(new Date().toString().slice(16, 21));

    if (sunAngleLightOffTime < currentTime && currentTime < (sunAngleLightOffTime + 90)){
        msg.hsSaturation = pctCalc(sunAngleLightOffTime, (sunAngleLightOffTime + 90), currentTime, true);
    } else if ((sunAngleLightOnTime - 90) < currentTime && currentTime < sunAngleLightOnTime) {
        msg.hsSaturation = pctCalc((sunAngleLightOnTime - 90), sunAngleLightOnTime, currentTime, false);
    } else {
