function setMessage(light, audioType, speaker) {
    if (alert_light) {
        msg.lightEffect = light;
    }
    if (alert_speaker) {
        msg[audioType] = speaker;
    }
}

function setText() {
    let alias = msg.windows.map(w => w.alias);
    if (alias.length == 1) {
        return " la fenêtre " + alias[0] + " ";
    } else {
        let joined = alias.join(", ");
        joined = joined.substring(0, joined.lastIndexOf(",")) + " et " + alias[alias.length - 1];
        return " les fenêtres " + joined + " ";
    }
}

let temperatures = global.get("temperatures") || {};
let humidities = global.get("humidities") || {};

if ("exterior" in temperatures && "exterior" in humidities) {
    let rosee_sensibility = 15 - parseInt(global.get("homeassistant").homeAssistant.states["input_number.rosee_sensibility"].state) * (12 / 100);
    let exterior_rosee = calcRoseePt(temperatures["exterior"].value, humidities["exterior"].value);
    let windows = msg.windows;
    let msgs = [];

    let windowsState = {};
    let windowsAreas = [];
    for (let w of windows) {
        if (w.linkedArea in temperatures) {
            if (!(w.linkedArea in windowsState)) {
                windowsState[w.linkedArea] = { ["on"]: [], ["off"]: [] };
            }
            windowsState[w.linkedArea][w.state].push(w);
        }
        if (!(windowsAreas.includes(w.linkedArea))) {
            windowsAreas.push(w.linkedArea);
        }
    }

    for (let area of windowsAreas) {
        if (area in temperatures && area in humidities) {
            let area_rosee = calcRoseePt(temperatures[area].value, humidities[area].value);
            if (temperatures["exterior"].value > temperatures[area].value && !temperatures[area].equalTemp && temperatures["exterior"].tendency >= 0) {
                temperatures[area].equalTemp = true;
                msgs.push({ "linkedArea": area, "alert": "equal" });
            } else if (temperatures["exterior"].value < temperatures[area].value && temperatures[area].equalTemp && temperatures["exterior"].tendency <= 0) {
                temperatures[area].equalTemp = false;
                msgs.push({ "linkedArea": area, "alert": "equal" });
            } else if (windowsState[area].on.length > 0 && exterior_rosee > area_rosee && temperatures[area].high && temperatures[area].tendency > 0) {
                msgs.push({ "linkedArea": area, "alert": "outside_max_temp_close", "windows": windowsState[area].on });
            } else if (windowsState[area].on.length > 0 && exterior_rosee < area_rosee && temperatures[area].low && temperatures[area].tendency < 0) {
                msgs.push({ "linkedArea": area, "alert": "outside_min_temp_close", "windows": windowsState[area].on });
            } else if (windowsState[area].on.length == 0 && exterior_rosee < area_rosee && temperatures[area].high && temperatures[area].tendency > 0) {
                msgs.push({ "linkedArea": area, "alert": "inside_max_temp_open", "windows": windowsState[area].off });
            } else if (windowsState[area].on.length == 0 && Math.abs(exterior_rosee - area_rosee) > rosee_sensibility && !temperatures[area].high) {
                msgs.push({ "linkedArea": area, "alert": "inside_max_hum_open", "windows": windowsState[area].off });
            } else {
                msgs.push({ "linkedArea": area, "alert": "" });
            }
        }
    }
    global.set("temperatures", temperatures);
    return [msgs];
}

function calcRoseePt(temperature, humidity) {
    let a = (17.27 * temperature) / (237.7 + temperature);
    return (237.7 * (a + Math.log(humidity / 100))) / (17.27 - (a + Math.log(humidity / 100)));
}

