var txt = "";
txt += "<data>";

// TEMPERATURE MAISON
var temp_int = global.get("temperatures").salon.value || "";
txt += wrap("temp_int", Math.round(temp_int * 10) / 10);

var temp_tend = global.get("temperatures").salon.tendency || "";
txt += wrap("temp_tend", temp_tend == 0 ? "" : temp_tend > 0 ? "↗" : "↘");

// MODE CHAUFFAGE
var heat_ = global.get("homeassistant").homeAssistant.states["input_boolean.heat"].state || "";
var heat_temp = global.get("homeassistant").homeAssistant.states["input_text.heat_temp"].state || "";
if (heat_ === "on" && heat_temp != "") {
    txt += wrap("warm", "Chauffage réglé " + (heat_temp != "Auto" ? "à " + heat_temp + "°C" : "en auto"));
} else {
    txt += wrap("warm", "");
}

// FENETRES
var windows_txt = global.get("windows_txt") || "";
txt += wrap("windows_txt", windows_txt);

// DETECTION
var detect_time = global.get("detect_txt") || "";
txt += wrap("detect_time", detect_time);

// STORES
var stores = global.get("stores_txt") || "";
txt += wrap("stores", stores);

// BATTERIE TEL
var tel_bat = global.get("homeassistant").homeAssistant.states["sensor.phone_bat_level"].state;
txt += wrap("tel_bat", tel_bat);

// BATTERIE TAB
var tab_bat = global.get("homeassistant").homeAssistant.states["sensor.lenovo_tb_x606f_bat_level"].state;
txt += wrap("tab_bat", tab_bat);

// WEATHER CONDITION
var day_night = global.get("homeassistant").homeAssistant.states["sun.sun"].state === "above_horizon" ? "-d" : "-n";
var weather_condition = global.get("homeassistant").homeAssistant.states["sensor.openweathermap_condition"].state;
weather_condition = (weather_condition == "sunny" || "clear-night" ? "clear" : weather_condition) + day_night;
txt += wrap("weather_condition", weather_condition);

// WEATHER TEMP
//var weather_temperature = global.get("homeassistant").homeAssistant.states["sensor.openweathermap_temperature"].state;
var weather_temperature = global.get("homeassistant").homeAssistant.states["input_number.temp_ext"].state || "";
txt += wrap("weather_temperature", Math.round(weather_temperature * 10) / 10);

function wrap(xname, x) {
    return "<" + xname + ">" + x + "</" + xname + ">";
}

txt += "</data>";
msg.payload = txt;
return msg;
