let linkedDevice = msg.linkedDevice;
let entity_id = msg.entity_id;
let state = msg.new_state;
let remote = msg.remote;
let msgs = [];

if (typeof remote == "object") {
    if ("increase" in remote && "decrease" in remote) {
        let diff = msg.new_state - msg.old_state;
        setStateMsgs([Math.abs(diff), (diff < 0 ? remote["decrease"] : remote["increase"])]);
        return [msgs, null];
    } else if ("on" in remote && "off" in remote) {
        setStateMsgs(remote[state].replaceAll(" ", "").split(","));
        return [msgs, null];
    }
} else if (typeof remote == "string" && state == "on") {
    setStateMsgs(remote.replaceAll(" ", "").split(","));
    msg.state = "off";
    return [msgs, msg];
}

function setStateMsgs(split) {
    let loop = parseInt(split[0]);
    let command = split[1];
    for (let i = 0; i < loop; i++) {
        msgs.push({
            payload: {
                data: {
                    entity_id: linkedDevice,
                    command: command
                }
            }
        });
    }
}

