    return msg.action_value == 0 ? "" : msg.action_value > 0 ? msg.action_value < msg.rotate_limit ? 'rotate_pos' : 'rotate_pos_hard' : msg.action_value > -msg.rotate_limit ? 'rotate_neg' : 'rotate_neg_hard';
