function lights(rotate) {
    if (rotate == "rotate_pos") {
        cubes[msg.entity_id].lightPower = cubes[msg.entity_id].lightPower + 1 > 4 ? 0 : cubes[msg.entity_id].lightPower + 1;
        msg.lightPower = lightPowerOptions[cubes[msg.entity_id].lightPower];
        msg.lightColor = lightColorOptions[cubes[msg.entity_id].lightColor];
        msg.colorTemp = 300;
    } else if (rotate == "rotate_neg") {
        cubes[msg.entity_id].lightPower = cubes[msg.entity_id].lightPower - 1 < 0 ? 4 : cubes[msg.entity_id].lightPower - 1;
        msg.lightPower = lightPowerOptions[cubes[msg.entity_id].lightPower];
        msg.lightColor = lightColorOptions[cubes[msg.entity_id].lightColor];
        msg.colorTemp = 300;
    } else if (rotate == "rotate_pos_hard") {
        cubes[msg.entity_id].move = true;
        msg.lightPower = lightPowerOptions[cubes[msg.entity_id].lightPower];
        cubes[msg.entity_id].lightColor = cubes[msg.entity_id].lightColor + 1 > 9 ? 0 : cubes[msg.entity_id].lightColor + 1;
        msg.lightColor = lightColorOptions[cubes[msg.entity_id].lightColor];
        msg.colorTemp = 300;
    } else if (rotate == "rotate_neg_hard") {
        cubes[msg.entity_id].move = true;
        msg.lightPower = lightPowerOptions[cubes[msg.entity_id].lightPower];
        cubes[msg.entity_id].lightColor = cubes[msg.entity_id].lightColor - 1 < 0 ? 9 : cubes[msg.entity_id].lightColor - 1;
        msg.lightColor = lightColorOptions[cubes[msg.entity_id].lightColor];
        msg.colorTemp = 300;
    }
}

