if ("exterior" in temperatures && "exterior" in humidities && temperatures["exterior"].online && humidities["exterior"].online) {
    let msgs = [];
    let windowsState = {};
    for (let w of msg.windows) {
        if (w.linkedArea in temperatures && w.linkedArea in humidities) {
            if (!(w.linkedArea in windowsState)) {
                windowsState[w.linkedArea] = { "on": [], "off": [] };
            }
            windowsState[w.linkedArea][w.state].push(w);
        }
    }

    for (let area of Object.keys(windowsState)) {
        if (area in temperatures && area in humidities && temperatures[area].online && humidities[area].online) {
            if (temperatures[area].value < temperatures["exterior"].value && !temperatures[area].equalTemp && temperatures["exterior"].tendency > 0) {
                temperatures[area].equalTemp = true;
                msgs.push({ "linkedArea": area, "alert": "equal" });
            } else if (temperatures[area].value > temperatures["exterior"].value && temperatures[area].equalTemp && temperatures["exterior"].tendency < 0) {
                temperatures[area].equalTemp = false;
                msgs.push({ "linkedArea": area, "alert": "equal" });
            } else if ( 
                in_home_zone
                && windowsState[area].on.length > 0
                && temperatures[area].high
                && comforts[area].ext_comfort > comforts[area].area_comfort
                && temperatures[area].tendency > 0
            ) {
                msgs.push({ "linkedArea": area, alert: "outside_max_temp_close", "windows": windowsState[area].on });
            } else if (
                in_home_zone
                && windowsState[area].on.length > 0
                && temperatures[area].low
                && comforts[area].ext_comfort < comforts[area].area_comfort
                && temperatures[area].tendency < 0
            ) {
                msgs.push({ "linkedArea": area, alert: "outside_min_temp_close", windows: windowsState[area].on });
            } else if (
                in_home_zone
                && windowsState[area].on.length === 0
                && temperatures[area].high
                && comforts[area].ext_comfort < comforts[area].area_comfort
            ) {
                msgs.push({ "linkedArea": area, alert: "inside_max_temp_open", windows: windowsState[area].off });
            } else if (
                in_home_zone
                && windowsState[area].on.length === 0
                && !temperatures[area].high
                && !temperatures[area].low
                && humidities[area].high
            ) {
                msgs.push({ "linkedArea": area, alert: "inside_max_hum_open", windows: windowsState[area].off });
            } else {
                msgs.push({ "linkedArea": area, alert: "" });
            }
        }
    }
    global.set("temperatures", temperatures);
    return [msgs];
}

