function getUp(type){
    totalUp += parseInt(activities[type][0]);
    return activities[type][0];
}

function getDo(type){
    totalDo += parseInt(activities[type][1]);
    return activities[type][1];
}

function getPercent(type,name){
    th += '<th style="width:20%">' + name + '</th>';
    return getUp(type) + "/" + getDo(type);
}

function buildHtml(i){
    return '<td>' + (i.split("/")[0] === i.split("/")[1] ? '✅': i) + '</td>';
}

