    if ("alarm" in persons[key] && persons[key].alarm == msg.currentTime) {
        return msg;
    }
}

let persons = global.get("persons") || {};

for (let key of Object.keys(persons)) {
    let validAlarm = "alarm" in persons[key] && persons[key].alarm.search(/[0-9]{4}-[0-9]{2}-[0-9]{2} [0-9]{2}:[0-9]{2}/g) != -1;
    if (validAlarm && persons[key].alarm.split(" ")[0] != msg.payload) {
        return msg;
    }
}

