let linkedDevice = msg.linkedDevice;
let entity_id = msg.entity_id;
let new_state = msg.new_state;
let old_state = msg.old_state;
let remote = msg.remote;
let msgs = [];

if ("increase" in remote && "decrease" in remote) {
    let diff = new_state - old_state;
    setStateMsgs({ "num_repeats": Math.abs(diff), "command": (diff < 0 ? remote["decrease"] : remote["increase"]) });
    return [msgs, null];
} else if (new_state in remote) {
    setStateMsgs(remote[new_state]);
    return [msgs, "off" in remote ? null : { "state": "off" }];
}

function setStateMsgs(remote_state) {
    if ("command" in remote_state && remote_state.command.startsWith("b64:")){
        let num_repeats = "num_repeats" in remote_state ? remote_state.num_repeats : 0;
        let delay_secs = "delay_secs" in remote_state ? remote_state.delay_secs : 0
        msgs.push({
            payload: {
                data: {
                    entity_id: linkedDevice,
                    command: remote_state.command,
                    num_repeats: num_repeats,
                    delay_secs: delay_secs
                }
            }
        });
    }
}

