msg.search = { "is": { linkedClass: "light", linkedArea: msg.linkedArea, state: ["on", "off"] } };
msg.output_name = "lights";
msg.outputEmptyResults = false;

return msg;

let count_check = msg.lightEffect?.count;
let rgbs_check = msg.lightEffect?.rgbs;

if (
    typeof count_check === "number" && count_check > 0
    && Array.isArray(rgbs_check) && rgbs_check.length == 2
    && Array.isArray(rgbs_check[0]) && rgbs_check[0].length == 3
    && Array.isArray(rgbs_check[1]) && rgbs_check[1].length == 3
) {

    let motion_areas = global.get("motion_areas") || {};
    let msgs = [];

    Object.entries(motion_areas).forEach(([key, value]) => {
        if (value != "off") {
            msgs.push({ linkedArea: key, lightEffect: msg.lightEffect });
        }
    });

    return [msgs];
}

let locked_lights = global.get("locked_lights") ?? [];
let lights = msg.lights;
let msgs = [];

for (let light of lights) {
    let supported_color_modes = light?.attributes?.supported_color_modes ?? [];
    let color_modes = ["hs", "rgb", "xy", "color_temp"];
    let support_color = (color_modes.find(a => supported_color_modes.includes(a)) ?? false);

    if (support_color && (!(locked_lights.includes(light.entity_id)) || locked_lights.includes(light.entity_id) && light.state === "on")) {
        let message = {};

        for (let i = 0, j = msg.lightEffect.count * 2 - 1; i < j; i++) {
            message = {
                light: light.entity_id,
                service: "on",
                payload: { data: { brightness: Math.max(...msg.lightEffect.rgbs[i % 2]), transition: 0.5 } },
                delay: i * 1500
            };

            if (color_modes) {
                message["payload"]["data"]["rgb_color"] = msg.lightEffect.rgbs[i % 2];
            }
            msgs.push({ ...message });
        }

        message = {
            light: light.entity_id,
            service: light.state,
            payload: { data: { transition: 0.5 } },
            delay: (msg.lightEffect.count * 2 - 1) * 1500
        };

        if (light.state == "on") {
            message["payload"]["data"]["brightness"] = light.attributes.brightness;
            if (light.attributes.color_mode == "color_temp") {
                message["payload"]["data"]["color_temp"] = light.attributes.color_temp;
            } else {
                message["payload"]["data"]["rgb_color"] = light.attributes.rgb_color;
            }
        }
        msgs.push({ ...message });
    }
}

return [msgs];

let persons = msg.persons;
let msgs = [];

if (msg.error === true) {
    persons = msg.persons.filter(p => (p.notifyMobileAppOnErrors ?? false));
} else if (Array.isArray(msg.sendTo)) {
    persons = msg.persons.filter(p => msg.sendTo.includes(p.name));
}

persons = persons.filter(p => p.mobileAppName !== "");
for (let p of persons) {
    let mess = {
        service: p.mobileAppName,
        payload: {
            data: { message: msg.message ?? "" }
        }
    };

    if (msg.title) {
        mess.payload.data.title = msg.title;
    }

    if (msg.data) {
        mess.payload.data["data"] = msg.data;
    } else {
        mess.payload.data["data"] = { actions: [{ "action": msg.notification_id, "title": "Effacer" }] };
    }
    msgs.push(mess);
}

return [msgs];

if (msg.sendMobile === true) {
    msg.search = { "is": { linkedClass: "person" } };
    msg.output_name = "persons";
    msg.outputEmptyResults = false;
    return msg;
}

let acceptedAudioType = ["speak", "sound", "link"];

if (msg.audio_type === undefined) {
    let audio_type = Object.keys(msg).find(a => acceptedAudioType.includes(a));
    if (audio_type !== undefined) {

        let speaker_msg = {
            audio_type: audio_type,
            [audio_type]: msg[audio_type],
            search: { "is": { linkedClass: "speaker" } },
            output_name: "speakers",
            outputEmptyResults: false,
            lightEffect: msg.lightEffect
        };

        if (typeof msg.volumeLevel == "number") {
            speaker_msg["volumeLevel"] = msg.volumeLevel;
        }
        return [speaker_msg, null];
    }
} else {
    return [null, msg];
}

let speaker_day_night_mode = global.get("homeassistant").homeAssistant.states["input_boolean.speaker_day_night_mode"].state == "on";
let linkedArea = msg.linkedArea ?? "";
let audio_type = msg.audio_type ?? "";
let speakers = msg.speakers;
let msgs = [];

if (msg.volumeLevel === undefined) {
    if (speaker_day_night_mode) {
        msg.volumeLevel = parseInt(global.get("homeassistant").homeAssistant.states["input_number.speaker_day_mode_volume"].state) / 100;
    } else {
        msg.volumeLevel = parseInt(global.get("homeassistant").homeAssistant.states["input_number.speaker_night_mode_volume"].state) / 100;
    }
}

if (speakers.length == 1) {
    setSpeaker(speakers[0]);
} else if (speakers.filter(s => s.linkedArea == linkedArea).length != 0) {
    setSpeaker(speakers.filter(s => s.linkedArea == linkedArea));
}
return [msgs];

function setSpeaker(speaker) {
    msgs.push({
        entity_id: speaker.entity_id,
        volumeLevel: msg.volumeLevel,
        defaultVolumeLevel: speaker.defaultVolumeLevel,
        content: msg[audio_type],
        audio_type: audio_type,
        lightEffect: msg.lightEffect
    });
}

let home_configuration = global.get("home_configuration") ?? {};
let tts_voice = home_configuration.tts_voice ?? "";

if (msg.audio_type == "speak" && tts_voice) {
    msg.payload = {
        domain: "tts",
        service: tts_voice,
        data: {
            entity_id: msg.entity_id,
            message: msg.content
        }
    };
    return msg;
} else if (msg.audio_type == "sound") {
    msg.payload = {
        domain: "media_player",
        service: "play_media",
        data: {
            entity_id: msg.entity_id,
            media_content_id: "media-source://media_source/media/" + msg.content,
            media_content_type: "music"
        }
    };
    return msg;
} else if (msg.audio_type == "link") {
    msg.payload = {
        domain: "media_player",
        service: "play_media",
        data: {
            entity_id: msg.entity_id,
            media_content_id: msg.content,
            media_content_type: "music"
        }
    };
    return msg;
}

let speakers = global.get("speakers") ?? {};

if (speakers[msg.entity_id] === undefined) {
    speakers[msg.entity_id] = {};
    speakers[msg.entity_id].status = "ready";
    speakers[msg.entity_id].queue = [];
}

if (speakers[msg.entity_id].status === "ready") {
    speakers[msg.entity_id].status = "not_ready";
    global.set("speakers", speakers);
    return msg;
}

if (speakers[msg.entity_id].status === "not_ready") {
    if (msg.speaker?.attributes?.media_duration === undefined || msg.speaker.attributes.media_duration > 15) {
        return msg;
    } else {
        speakers[msg.entity_id].queue.push({ ...msg });
        global.set("speakers", speakers);
    }
}

if (msg.entity_id) {
    let switchDomain = ["input_boolean", "switch", "light"];
    let valueDomain = ["input_number", "input_text"];
    let selectDomain = ["input_select", "select"];
    msg.domain = msg.entity_id.split(".")[0];

    if (switchDomain.includes(msg.domain) && ["on", "off"].includes(msg.state)) {
        msg.service = "turn_" + msg.state;
        msg.payload = {
            data: {
                entity_id: msg.entity_id
            }
        };
        return [msg, null];
    } else if (selectDomain.includes(msg.domain) && msg.state !== undefined) {
        msg.service = "select_option";
        msg.payload = {
            data: {
                entity_id: msg.entity_id, option: msg.state
            }
        };
        return [msg, null];
    } else if (valueDomain.includes(msg.domain) && (msg.domain == "input_number" && !isNaN(msg.state) || msg.domain == "input_text")) {
        msg.service = "set_value";
        msg.payload = {
            data: {
                entity_id: msg.entity_id, value: msg.state
            }
        };
        return [msg, null];
    } else if (msg.domain === "input_datetime") {
        let type =
            msg.state.search(/[0-9]{4}-[0-9]{2}-[0-9]{2} [0-9]{2}:[0-9]{2}/) == 0 ? "datetime"
                : msg.state.search(/[0-9]{4}-[0-9]{2}-[0-9]{2}/) == 0 ? "date"
                    : msg.state.search(/[0-9]{2}:[0-9]{2}/) == 0 ? "time" : "";
        if (type) {
            msg.service = "set_datetime";
            msg.payload = {
                data: {
                    entity_id: msg.entity_id, [type]: msg.state
                }
            };
            return [msg, null];
        }
    } else if (msg.domain === "action") {
        return [{ ...msg.state }, null];
    } else if (msg.domain === "emulate") {
        msg.payload = {
            event: "emulate",
            data: {
                entity_id: msg.entity_id.replace(msg.domain + ".", ""),
                ...msg.state
            }
        };
        return [null, msg];
    }
}

if (msg.error?.message !== undefined) {
    let allowedError = msg.blockedErrors.find(e => msg.error.message.includes(e)) === undefined;
    if (allowedError) {
        let notifications = global.get("notifications") ?? {};

        msg.alertType = "error";
        msg.title = "Erreur détéctée";
        msg.notification_id = (msg.flowTitle + "_" + msg.error.source.name).replaceAll(/[^A-Za-z0-9_]/g, "").replaceAll(/(_){2,}/g, "_").replaceAll(" ", "_");
        msg.message = "Erreur : " + msg.flowTitle + " > " + msg.error.source.name + " > " + msg.error.message;

        if (notifications[msg.notification_id] !== undefined) {
            msg.message += ", apparue " + (notifications[msg.notification_id].count + 1) + " fois.";
        } else {
            msg.sendMobile = true;
        }
        msg.error = true;
        return msg;
    }
}

if (msg.error?.source?.name !== undefined) {
    let allowedError = msg.blockedErrors.filter(error => msg.error.message.includes(error)).length == 0;
    if (allowedError) {
        msg.payload = { ...msg };
        return msg;
    }
}

msg.blockedErrors = [
    "<class 'TimeoutError'>",
    "The write socket is closed",
    "client quota exceeded"
];

return msg;

let notifications = global.get("notifications") ?? {};
if (notifications[msg.notification_id] === undefined || msg.override) {
    let d = new Date();
    let local_time = (d.getMonth() + 1) + " " + d.getDate() + " " + d.getHours() + ":" + (d.getMinutes() < 10 ? "0" + d.getMinutes() : d.getMinutes());

    notifications[msg.notification_id] = {};
    notifications[msg.notification_id].count = 1;
    notifications[msg.notification_id].creationDate = msg.restore ? msg.creationDate : local_time;
} else {
    notifications[msg.notification_id].count++;
}

notifications[msg.notification_id].title = msg.title ?? "";
notifications[msg.notification_id].message = msg.message ?? "";
notifications[msg.notification_id].alertType = msg.alertType ?? "info";

if (notifications[msg.notification_id].count <= 10) {
    global.set("notifications", notifications);
    msg.notification = notifications[msg.notification_id];
    msg.payload = notifications;
    return msg;
}

if (msg.notification?.event?.service === "create") {
    return [msg, null];
} else if (msg.notification?.event?.service === "dismiss") {
    let notifications = global.get("notifications") ?? {};
    delete notifications[msg.notification.event.service_data.notification_id];
    msg.payload = notifications;
    return [null, msg];
}

let notifications = global.get("notifications") ?? {};

if (msg.notification_id in notifications) {
    return msg;
}

if (msg.notification_id ?? false) {
    if (msg.dismiss ?? false) {
        return [null, msg];
    } else {
        return [msg, null];
    }
}

let locked_lights = global.get("locked_lights") ?? [];

if (msg.lock !== undefined && msg.lock_lights !== undefined) {
    locked_lights = msg.lock ?
        [... new Set([...locked_lights, ...msg.lock_lights])] :
        locked_lights.filter(l => !msg.lock_lights.includes(l));

    global.set("locked_lights", locked_lights);
    delete msg.lock_lights;
    delete msg.lock;
    return msg;
} else if (typeof msg.entity_id === "string") {
    if (!locked_lights.includes(msg.entity_id)) {
        return msg;
    }
}

msg.lock_lights = msg.linkedLights;
msg.lock = msg.cube.flip90;

return msg;

msg.lock = !msg.restore;
msg.lock_lights = msg.linkedLights;
return msg;

if ((msg.lights?.length ?? 0) != 0 && msg.ambiance_config?.ambiances_type !== undefined) {
    msg.reset = true;
    if (msg.action_type != "stop") {
        let msgs = [];
        for (let light of msg.lights) {
            if (light.attributes?.supported_color_modes?.includes("rgb") !== undefined) {
                let ambiance_type = msg.ambiance_config.ambiances_type[msg.action_type];
                let hs = hsRandomValue(ambiance_type.color[0], Math.round(Math.random() * ambiance_type.variance[0]), 0, 360);
                let saturation = randomValue(ambiance_type.color[1], Math.round(Math.random() * ambiance_type.variance[1]), 0, 100);
                let brightness = randomValue(ambiance_type.color[2], Math.round(Math.random() * ambiance_type.variance[2]), 0, 100);
                let delay = 1000 + Math.round(Math.random() * 14000);
                msgs.push({
                    entity_id: light.entity_id,
                    action_type: msg.action_type,
                    ambiance_config: msg.ambiance_config,
                    delay: delay,
                    payload: {
                        data: {
                            entity_id: light.entity_id,
                            hs_color: [hs, saturation],
                            brightness_pct: brightness,
                            transition: Math.round(delay / 100) / 10
                        }
                    }
                });
            }
        }
        return [msgs, msg];
    } else {
        return [null, msg];
    }
}

function hsRandomValue(value, variance, lowRange, highRange) {
    let random = Math.random();
    return random < 0.5 ? (value - variance < lowRange ? highRange - variance : value - variance) : (value + variance > highRange ? lowRange + variance : value + variance);
}

function randomValue(value, variance, lowRange, highRange) {
    let random = Math.random();
    return random < 0.5 ? (value - variance < lowRange ? lowRange : value - variance) : (value + variance > highRange ? highRange : value + variance);
}

if (msg.action_type != "stop") {
    let ambiance_type = msg.ambiance_config.ambiances_type[msg.action_type];
    let hs = hsRandomValue(ambiance_type.color[0], Math.round(Math.random() * ambiance_type.variance[0]), 0, 360);
    let saturation = randomValue(ambiance_type.color[1], Math.round(Math.random() * ambiance_type.variance[1]), 0, 100);
    let brightness = randomValue(ambiance_type.color[2], Math.round(Math.random() * ambiance_type.variance[2]), 0, 100);
    let delay = 1000 + Math.round(Math.random() * 14000);

    msg.delay = delay;
    if (msg.action_type == "thunderstorm" && delay < 1500) {
        msg.payload = {
            data: {
                entity_id: msg.entity_id,
                effect: "Strobe epilepsy!"
            }
        };        
    } else {
        msg.payload = {
            data: {
                entity_id: msg.entity_id,
                hs_color: [hs, saturation],
                brightness_pct: brightness,
                transition: Math.round(delay / 100) / 10
            }
        };
    }

    return msg;
}

function hsRandomValue(value, variance, lowRange, highRange) {
    let random = Math.random();
    return random < 0.5 ? (value - variance < lowRange ? highRange - variance : value - variance) : (value + variance > highRange ? lowRange + variance : value + variance);
}

function randomValue(value, variance, lowRange, highRange) {
    let random = Math.random();
    return random < 0.5 ? (value - variance < lowRange ? lowRange : value - variance) : (value + variance > highRange ? highRange : value + variance);
}

