function dateToDisplay(notifCreationDate) {
    let monthTradFr = {
        1: "Janvier",
        2: "Février",
        3: "Mars",
        4: "Avril",
        5: "Mai",
        6: "Juin",
        7: "Juillet",
        8: "Août",
        9: "Septembre",
        10: "Octobre",
        11: "Novembre",
        12: "Décembre"
    };

    let notifCreationDateSplit = notifCreationDate.split(" ");
    let notifMonth = parseInt(notifCreationDateSplit[0]);
    let notifDay = parseInt(notifCreationDateSplit[1]);
    let notifHour = notifCreationDateSplit[2];

    if (currentDay == notifDay && currentMonth == notifMonth) {
        return notifHour + " : ";
    } else if (currentDay - notifDay == 1 && currentMonth == notifMonth) {
        return "Hier à " + notifHour + " : ";
    } else if (currentDay - notifDay == 2 && currentMonth == notifMonth) {
        return "Avant-hier à " + notifHour + " : ";
    } else {
        return "Le " + notifDay + " " + monthTradFr[notifMonth] + " à " + notifHour + " : ";
    }
}

