function night_brightnessPctCalc() {
    let sun_light_time_dusk = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_dusk"].state);
    let sun_light_time_midnight = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_midnight"].state);
    let sun_light_time_dawn = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_dawn"].state);
    let sun_light_time_dawn_angle = in_minutes(global.get('homeassistant').homeAssistant.states["input_datetime.sun_light_time_dawn_angle"].state);

    // Pivot
    sun_light_time_rising = sun_light_time_rising + 1440 - sun_light_time_setting;
    sun_light_time_dawn_angle = sun_light_time_dawn_angle + 1440 - sun_light_time_setting;
    sun_light_time_dawn = sun_light_time_dawn + 1440 - sun_light_time_setting;
    sun_light_time_midnight = sun_light_time_dusk < sun_light_time_midnight ? sun_light_time_midnight - sun_light_time_setting : sun_light_time_midnight + 1440 - sun_light_time_setting;
    sun_light_time_dusk = sun_light_time_dusk - sun_light_time_setting;
    currentTime = sun_light_time_setting <= currentTime ? currentTime - sun_light_time_setting : 1440 - sun_light_time_setting + currentTime;
    sun_light_time_setting = 0;

    // Choix de l'heure de l'aube la plus petite 
    sun_light_time_dawn = sun_light_time_dawn < sun_light_time_dawn_angle ? sun_light_time_dawn : sun_light_time_dawn_angle;

    if (sun_light_time_setting <= currentTime && currentTime < sun_light_time_dusk) {
        msg.currentTimeStatus = pctCalc(sun_light_time_setting, sun_light_time_midnight, currentTime, false);
        return function (light) { return brightnessPctCalc(pctCalc(sun_light_time_setting, sun_light_time_dusk, currentTime, false), light) };
    } else if (sun_light_time_dusk <= currentTime && currentTime < sun_light_time_midnight - 60) {
        msg.currentTimeStatus = pctCalc(sun_light_time_setting, sun_light_time_midnight, currentTime, false);
        return function (light) { return light.brightRange[1] };
    } else if (sun_light_time_midnight - 60 <= currentTime && currentTime < sun_light_time_midnight) {
        msg.currentTimeStatus = pctCalc(sun_light_time_setting, sun_light_time_midnight, currentTime, false);
        return function (light) { return brightnessPctCalc(pctCalc(sun_light_time_midnight - 60, sun_light_time_midnight, currentTime, true), light) };
    } else if (sun_light_time_midnight <= currentTime && currentTime < sun_light_time_dawn - 60) {
        msg.currentTimeStatus = 100;
        return function (light) { return light.nightLight ? light.brightRange[0] : 0 };
    } else if (sun_light_time_dawn - 60 <= currentTime && currentTime < sun_light_time_dawn) {
        msg.currentTimeStatus = pctCalc(sun_light_time_dawn - 60, sun_light_time_rising, currentTime, true);
        return function (light) { return brightnessPctCalc(pctCalc(sun_light_time_dawn - 60, sun_light_time_dawn, currentTime, true), light) };
    } else if (sun_light_time_dawn <= currentTime && currentTime < (sun_light_time_dawn + sun_light_time_rising) / 2) {
        msg.currentTimeStatus = pctCalc(sun_light_time_dawn - 60, sun_light_time_rising, currentTime, true);
        return function (light) { return light.brightRange[1] };
    } else if ((sun_light_time_dawn + sun_light_time_rising) / 2 <= currentTime && currentTime < sun_light_time_rising) {
        msg.currentTimeStatus = pctCalc(sun_light_time_dawn - 60, sun_light_time_rising, currentTime, true);
        return function (light) { return brightnessPctCalc(pctCalc((sun_light_time_dawn + sun_light_time_rising) / 2, sun_light_time_rising, currentTime, true), light) };
    } else {
        msg.currentTimeStatus = 0;
        return function (light) { return light.brightRange[0] };
    }
}

function cloudy_brightnessPctCalc() {
    let light_when_cloudy = global.get('homeassistant').homeAssistant.states["input_boolean.light_when_cloudy"].state == "on";
    if (light_when_cloudy) {
        let cloudy_light = parseInt(global.get('homeassistant').homeAssistant.states["input_number.cloudy_light"].state);
        let illuminances = global.get("illuminances") ?? {};
        if ("exterior" in illuminances && "value" in illuminances["exterior"] && illuminances["exterior"]["value"] < cloudy_light && sun.elevation > sun_light_angle_setting_rising) {
            return pctCalc(min_cloudyLight, cloudy_light, illuminances["exterior"]["value"], true);
        } else if ("exterior" in illuminances && "value" in illuminances["exterior"] && illuminances["exterior"]["value"] < cloudy_light && sun.elevation > sun_light_angle_dusk_dawn) {
            return pctCalc(min_cloudyLight, cloudy_light, illuminances["exterior"]["value"], true) * pctCalc(sun_light_angle_dusk_dawn, sun_light_angle_setting_rising, sun.elevation, false) / 100;
        } else {
            return 0;
        }
    }
}

function daylight_brightnessPctCalc(light) {
    if (sun.elevation > sun_light_angle_setting_rising) {
        return light.brightRange[1];
    } else if (sun.elevation > sun_light_angle_dusk_dawn) {
        return light.brightRange[1] * pctCalc(sun_light_angle_dusk_dawn, sun_light_angle_setting_rising, sun.elevation, false) / 100;
    } else {
        return 0;
    }
}

function brightnessPctCalc(brightnessPct, light) {
    brightnessPct = Math.cos((9 * brightnessPct / 10 - 90) * Math.PI / 180);
    return light.brightRange[0] + (light.brightRange[1] - light.brightRange[0]) * brightnessPct;
}

function pctCalc(min, max, nb, reverse) {
    return nb < min ? (reverse ? 100 : 0) : nb > max ? (reverse ? 0 : 100) : reverse ? (max - nb) / Math.abs(min - max) * 100 : (nb - min) / Math.abs(min - max) * 100;
}

function in_minutes(timeTxt) {
    timeTxt = timeTxt.split(":");
    return parseInt(timeTxt[0]) * 60 + parseInt(timeTxt[1]);
}

