        msgs.push({
            light: lights[i].entity_id,
            count: msg.lightEffect.count,
            hue1: msg.lightEffect.hue1,
            hue2: msg.lightEffect.hue2
        });
    }
}

return [msgs];

function supportHSColor(light) {
    for (let i = 0, j = light.attributes.supported_color_modes.length; i < j; i++) {
        if (light.attributes.supported_color_modes[i] == "hs") {
            return true;
        }
    }
    return false;
}

