            }
        };
    }

    return msg;
}

msg.filter = { "in": { entity_id: msg.linkedSpeaker } };
msg.result = "speaker";
return msg;

if (
    "speaker" in msg && msg.speaker.length != 0 &&
    "attributes" in msg.speaker[0] &&
    "device_class" in msg.speaker[0].attributes &&
    msg.speaker[0].attributes.device_class == "speaker") {

    if (msg.action_type == "stop") {
