function setMessage(windows) {
    let alias = [];
    for (let window of windows) {
        alias.push(window.alias);
    }
    if (alias.length == 1) {
        return " la fenêtre " + alias[0] + " ";
    } else {
        let joinAlias = alias.join(", ");
        joinAlias = joinAlias.substring(0, joinAlias.lastIndexOf(", ")) + " et " + joinAlias.substring(joinAlias.lastIndexOf(", ") + 2);
        return " les fenêtres " + joinAlias + " ";
    }
}

