let timeToOpen = "timeToOpen" in msg ? msg.timeToOpen : 0;
let oldState = msg.oldState;
let newState = msg.newState;

if (msg.linkedDevice != "") {
    msg.entity_id = msg.linkedDevice;
    if (newState == 100) {
        msg.service = "open_cover";
        msg.delay = (timeToOpen + timeToAeration) * 1000;
    } else if (newState == 0) {
        msg.service = "close_cover";
        msg.delay = (timeToClose + timeToAeration) * 1000;
    } else if (newState == 1) {
        if (oldState == 0) {
            msg.service = "open_cover";
            msg.delay = (timeToAeration) * 1000;
        } else {
            msg.service = "close_cover";
            msg.delay = (oldState + 1) * timeToClose * 10 + timeToAeration * 1000;
            msg.nextService = "open_cover";
            msg.nextDelay = (timeToAeration) * 1000;
        }
    } else {
        msg.delay = oldState == 0 ? timeToAeration : 0;
        if (oldState < newState) {
            msg.service = "open_cover";
            msg.delay += (newState - oldState) * timeToOpen * 10;
        } else {
            msg.service = "close_cover";
            msg.delay += (oldState - newState) * timeToClose * 10;
        }
    }
    return msg;
}

