function sendSubDevice(subDevices) {
    for (let subDevice of subDevices) {
        let state = global.get("homeassistant").homeAssistant.states[subDevice[0]].state;
        if (state != subDevice[1]) {
            msgs.push({
                entity_id: subDevice[0],
                state: subDevice[1],
                delay: subDevice.length == 3 ? subDevice[2] * 1000 : 0
            });
        }
    }
}

function logicalOperator(condition) {
    let resultOR = false;
    if (condition != "") {
        let operatorORSplit = condition.split("OR");
        let countORLength = operatorORSplit.length - 1;
        for (let i = 0; i < operatorORSplit.length; i++) {
            let operatorANDSplit = operatorORSplit[i].split("AND");
            let resultAND = true;
            for (let i = 0; i < operatorANDSplit.length; i++) {
                let operatorAND = operatorANDSplit[i];
                let csplit = operatorAND.trim().split(" ").slice(0, 2);
                csplit.push(operatorAND.substring(operatorAND.indexOf(csplit[1]) + csplit[1].length).trim());
                resultAND = resultAND && comparisonOperator(getHaState(csplit[0]), csplit[1], csplit[2]);
                if (countORLength == i && !resultAND) {
                    return resultAND;
                }
            }
            resultOR = resultOR || resultAND;
        }
    }
    return resultOR;
}

function comparisonOperator(haState, operator, userState) {
    userState = isNaN(userState) ? userState : parseFloat(userState);
    let result = false;
    if (operator == "==") {
        result = haState == userState;
    } else if (operator == "!=") {
        result = haState != userState;
    } else if (operator == "<") {
        result = haState < userState;
    } else if (operator == "<=") {
        result = haState <= userState;
    } else if (operator == ">") {
        result = haState > userState;
    } else if (operator == ">=") {
        result = haState >= userState;
    }
    return result;
}

function getHaState(haState) {
    if (haState.startsWith("global.")) {
        let ha_state_split = haState.replace("global.", "").split(".");
        let gbl_ha_state = global.get(ha_state_split[0]);
        for (let i = 1; i < ha_state_split.length; i++) {
            gbl_ha_state = gbl_ha_state[ha_state_split[i]];
        }
        return isNaN(gbl_ha_state) ? gbl_ha_state : parseFloat(gbl_ha_state);
    } else if (haState.startsWith("time")) {
        let date = new Date();
        let hours = date.getHours() < 10 ? "0" + date.getHours() : date.getHours();
        let minutes = date.getMinutes() < 10 ? "0" + date.getMinutes() : date.getMinutes();
        return hours + ":" + minutes;
    } else {
        let ha_state_split = haState.split(".");
        let gbl_ha_state = global.get("homeassistant").homeAssistant.states[ha_state_split[0] + "." + ha_state_split[1]];
        for (let i = 2; i < ha_state_split.length; i++) {
            gbl_ha_state = gbl_ha_state[ha_state_split[i]];
        }
        return isNaN(gbl_ha_state) ? gbl_ha_state : parseFloat(gbl_ha_state);
    }
}

