function calcRanges(onTime, offTime) {
    let start = time_to_minutes(onTime);
    let end = time_to_minutes(offTime);
    end = start > end ? 1440 + end : end;
    let times = { onTime: [], offTime: [] };
    for (let i = 0; i < (parseInt(end - start) / 60); i++) {
        times.onTime.push(minutes_to_time(start + (i + 1) * 60 - waterHeatPct * 60));
        times.offTime.push(minutes_to_time(start + (i + 1) * 60));
    }
    return times;
}

function calcIsScheduled(currentTime, device) {
    let isTime = false;
    if (device.onTime.length == device.offTime.length) {
        if (device.onTime.length == 0) {
            return true;
        }
        for (let i = 0; i < device.onTime.length; i++) {
            isTime = isTime
                || (device.onTime[i] < device.offTime[i] && device.onTime[i] <= currentTime && currentTime < device.offTime[i])
                || (device.onTime[i] > device.offTime[i] && (
                    device.onTime[i] <= currentTime && currentTime <= "23:59"
                    || "00:00" <= currentTime && currentTime < device.offTime[i]))
                ;
            if (isTime) {
                return [device.onTime[i], device.offTime[i]];
            }
        }
    }
    return isTime;
}

function time_to_minutes(timeTxt) {
    timeTxt = timeTxt.split(":");
    return parseInt(timeTxt[0]) * 60 + parseInt(timeTxt[1]);
}

function minutes_to_time(time) {
    time = time >= 1440 ? time - 1440 : time;
    let hours = parseInt(time / 60);
    hours = hours < 10 ? "0" + hours : hours == 0 ? "00" : hours;
    let minutes = time % 60;
    minutes = minutes < 10 ? "0" + minutes : minutes == 0 ? "00" : minutes;
    return hours + ":" + minutes;
}

